    private void T_CalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_T_CalcActionPerformed
        int T_n = Integer.parseInt(T_Numero.getText());
        //int tabuada = 0;
        for (int contador = 1; contador <= 10; contador++){
            int tabuada = T_n * contador;
            Integer.toString(T_n);
            Integer.toString(contador);
            Integer.toString(tabuada);
            T_Resultado.add(T_n + " x " + contador + " = " + tabuada);
        }
    }//GEN-LAST:event_T_CalcActionPerformed

