    private void Mult_CalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Mult_CalcActionPerformed
        double n = Double.parseDouble(Mult_Numero.getText());
        double duplo = n*2;
        double triplo = n*3;
        double quadr = n*4;
        double quint = n*5;
        Mult_Dobro_Res.setText(Double.toString(duplo));
        Mult_Triplo_Res.setText(Double.toString(triplo));
        Mult_Quadruplo_Res.setText(Double.toString(quadr));
        Mult_Quintuplo_Res.setText(Double.toString(quint));
    }//GEN-LAST:event_Mult_CalcActionPerformed

