    private void Med_CalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Med_CalcActionPerformed
        double MedR = (Double.parseDouble(Med_1valor.getText())+
            Double.parseDouble(Med_2valor.getText())+
            Double.parseDouble(Med_3valor.getText())+
            Double.parseDouble(Med_4valor.getText()))/4;
        Med_Res.setText(Double.toString(MedR));
    }//GEN-LAST:event_Med_CalcActionPerformed

